import React from 'react'
import { Header } from './Header'
import { SummaryCard } from './SummaryCard'
import { IncomeChart } from './IncomeChart'
import {
  Wallet,
  DollarSign,
  ClipboardCheck,
  CreditCard,
  FileText,
  BarChart2,
} from 'lucide-react'

export const Dashboard = () => {
  // Mock navigation function
  const handleCardClick = (destination) => {
    console.log(`Navigating to ${destination}`)
    // In a real app, you would use router.push() or similar
  }

  return (
    <div className="p-6 max-w-7xl mx-auto">
      <Header title="Dashboard" />

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mt-6">
        <SummaryCard
          title="Total Balance"
          value="632,000"
          icon={
            <div className="w-10 h-10 rounded-full bg-orange-100 flex items-center justify-center text-orange-500">
              <Wallet size={20} />
            </div>
          }
          change="+1.29%"
          changeType="positive"
          onClick={() => handleCardClick('balance')}
        />

        <SummaryCard
          title="Total Income"
          value="632,000"
          icon={
            <div className="w-10 h-10 rounded-full bg-blue-100 flex items-center justify-center text-blue-500">
              <DollarSign size={20} />
            </div>
          }
          change="+1.29%"
          changeType="positive"
          onClick={() => handleCardClick('income')}
        />

        <SummaryCard
          title="Inspection Estimate Generations Pendings"
          count={2}
          icon={
            <div className="w-10 h-10 rounded-full bg-green-100 flex items-center justify-center text-green-500">
              <ClipboardCheck size={20} />
            </div>
          }
          onClick={() => handleCardClick('inspection-estimates')}
        />

        <SummaryCard
          title="Payment Approval Pendings"
          count={3}
          icon={
            <div className="w-10 h-10 rounded-full bg-green-100 flex items-center justify-center text-green-500">
              <CreditCard size={20} />
            </div>
          }
          onClick={() => handleCardClick('payment-approvals')}
        />

        <SummaryCard
          title="Quotation Generations Pendings"
          count={3}
          icon={
            <div className="w-10 h-10 rounded-full bg-red-100 flex items-center justify-center text-red-500">
              <FileText size={20} />
            </div>
          }
          onClick={() => handleCardClick('quotations')}
        />

        <SummaryCard
          title="Extra"
          count={5}
          icon={
            <div className="w-10 h-10 rounded-full bg-red-100 flex items-center justify-center text-red-500">
              <BarChart2 size={20} />
            </div>
          }
          onClick={() => handleCardClick('extra')}
        />
      </div>

      <IncomeChart />
    </div>
  )
}
